import {List, Avatar, Icon} from 'antd';
import React, {Component} from 'react';
import './App.css';
import axios from 'axios';
import Md from './md';
import { Route, Link } from 'react-router-dom';
// import {withRouter} from 'react-router-dom';
axios.defaults.withCredentials = true
axios.defaults.headers.post['Content-Type'] = 'application/json';

// const server = "http://127.0.0.1:8000";
// import axios from "axios";
// const listData = [];



const IconText = ({ type, text }) => (
    <span>
      <Icon type={type} style={{ marginRight: 8 }} />
      {text}
    </span>
  );
class List_blog extends Component{
  constructor(){
    super()
    this.state={
      totalData:'',
      listData:[],
      md:''
    }
  }
  handleclick(){
    fetch('./data.json', {
      method: "GET",
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
        },
     
    })
    .then(response => response.json())//解析为Promise
    .then(data => {
    
    this.setState({totalData: data})  ////赋值到本地数据
  })
  }
  onclick(){
    // this.props.history.push("/md");
  }
  handleinit(){
    for (let i = 0; i < 1; i++) {
      this.state.listData.push({
        // href: onclick,
        title: `数据库大作业任务目标`,
        avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
        description:
          '数据库大作业的项目要求翻译',
        content:
          'We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.',
      });
    }
    var _getAllFilesFromFolder = function(dir) {

      var filesystem = require("fs");
      var results = [];
  
      filesystem.readdirSync('./markdown').forEach(function(file) {
  
          file = './markdown'+'/'+file;
          var stat = filesystem.statSync(file);
  
          if (stat && stat.isDirectory()) {
              results = results.concat(_getAllFilesFromFolder(file))
          } else results.push(file);
  
      });
  
      return results;
  
  };
  console.log(_getAllFilesFromFolder)
    // var recursive = require("recursive-readdir");
    
    // recursive("./markdown", function (err, files) {
    //     // `files` is an array of file paths
    //   console.log(files);
    // });
  }
componentDidMount(){
  this.handleclick()
  this.handleinit()
}
    render(){
        return(
            <List
            itemLayout="vertical"
            size="large"
            pagination={{
              onChange: page => {
                console.log(page);
              },
              pageSize: 3,
            }}
            dataSource={this.state.listData}
            footer={
              <div>
                <b>butter&bentleyoma</b> 
              </div>
            }
            renderItem={item => (
              <Link to='/article'>
              <List.Item 
                key={item.title}
                actions={[
                  <IconText type="star-o" text="156" key="list-vertical-star-o" />,
                  <IconText type="like-o" text="156" key="list-vertical-like-o" />,
                  <IconText type="message" text="2" key="list-vertical-message" />,
                ]}
                extra={
                  <img
                    width={272}
                    alt="logo"
                    src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
                  />
                }
              >
                <List.Item.Meta
                />
                {item.content}
              </List.Item>
              <Route component={Md} path='/article'/>
              </Link>
              
            )}
          /> 
        )
    }
    mountNode
}
export default List_blog
