import {List, Avatar, Icon} from 'antd';
import React, {Component} from 'react';
import './App.css';
import axios from 'axios';
import Md from './md';
import { Route, Link } from 'react-router-dom';
// import {withRouter} from 'react-router-dom';
axios.defaults.withCredentials = true
axios.defaults.headers.post['Content-Type'] = 'application/json';



const IconText = ({ type, text }) => (
    <span>
      <Icon type={type} style={{ marginRight: 8 }} />
      {text}
    </span>
  );
class List_blog extends Component{
  constructor(){
    super()
    this.state={
      totalData:'',
      listData:[],
      md:''
    }
  }
  handleclick(){
    fetch('./data.json', {
      method: "GET",
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
        },
     
    })
    .then(response => response.json())//解析为Promise
    .then(data => {
    
    this.setState({totalData: data})  ////赋值到本地数据
    for(let i=0; i<this.state.totalData['data'].length; i++){
      this.state.listData.push({
        title:"数据库大作业",
        content:this.state.totalData['data'][i],
        order:1,
      });
    }
    console.log(this.state.listData); 
  })
  }
componentDidMount(){
  this.handleclick()
}
    render(){
        return(
            <List
            itemLayout="vertical"
            size="large"
            pagination={{
              onChange: page => {
                console.log(page);
              },
              pageSize: 3,
            }}
            dataSource={this.state.totalData['data']}
            footer={
              <div>
                <b>butter&bentleyoma</b> 
              </div>
            }
            renderItem={item => (
              <Link to='/article'>
              <List.Item 
                key='数据库大作业'
                actions={[
                  <IconText type="star-o" text="156" key="list-vertical-star-o" />,
                  <IconText type="like-o" text="156" key="list-vertical-like-o" />,
                  <IconText type="message" text="2" key="list-vertical-message" />,
                ]}
                extra={
                  <img
                    width={272}
                    alt="logo"
                    src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
                  />
                }
              >
                <List.Item.Meta
                />
                {item}
                {/* {item.content}s */}
              </List.Item>
              <Route component={Md} path='/article'/>
              </Link>
              
            )}
          /> 
        )
    }
    mountNode
}
export default List_blog
