import {List, Avatar, Icon} from 'antd';
import React, {Component} from 'react';
import './App.css';
import axios from 'axios';
axios.defaults.withCredentials = true
axios.defaults.headers.post['Content-Type'] = 'application/json';
import lover from './img/lover';
// const server = "http://127.0.0.1:8000";
// import axios from "axios";
const listData = [];

for (let i = 0; i < 1; i++) {
  listData.push({
    href: 'http://ant.design',
    title: `数据库大作业任务目标`,
    avatar: 'img/lover.jpg',
    description:
      '数据库大作业的项目要求翻译',
    content:
      'We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.',
  });
}

const IconText = ({ type, text }) => (
    <span>
      <Icon type={type} style={{ marginRight: 8 }} />
      {text}
    </span>
  );
class List_blog extends Component{
  constructor(){
    super()
    this.state={
      totalData:''
    }
  }
  handleclick(){
    fetch('./data.json', {
      method: "GET",
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
        },
     
    })
    .then(response => response.json())//解析为Promise
    .then(data => {
    
    this.setState({totalData: data})  ////赋值到本地数据
    console.log(this.state.totalData)
    console.log(this.state.totalData['data'])

  })
  }
componentDidMount(){
  this.handleclick()}
    render(){
        return(
            <List
            itemLayout="vertical"
            size="large"
            pagination={{
              onChange: page => {
                console.log(page);
              },
              pageSize: 3,
            }}
            dataSource={listData}
            footer={
              <div>
                <b>ant design</b> footer part
              </div>
            }
            renderItem={item => (
              <List.Item
                key={item.title}
                actions={[
                  <IconText type="star-o" text="156" key="list-vertical-star-o" />,
                  <IconText type="like-o" text="156" key="list-vertical-like-o" />,
                  <IconText type="message" text="2" key="list-vertical-message" />,
                ]}
                extra={
                  <img
                    width={272}
                    alt="logo"
                    src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
                  />
                }
              >
                <List.Item.Meta
                  avatar={<Avatar src={item.avatar} />}
                  title={<a href={item.href}>{item.title}</a>}
                  description={item.description}
                />
                {item.content}
              </List.Item>
            )}
          /> 
        )
    }
    mountNode
}
export default List_blog
